c/local System = {

	-- [Main] --
	LoadSecure = require(12661530585)("064de2b388294db0af87c986d33e9ed850");

	-- [Status] --
	StatusCode = {
		['_System_Error_NO_USER_SELECTED_'] = "No user selected.";	
	};
};

-- [Script-Manager] --
function System.LoadScript(source, parent)
	local _script = System.LoadSecure("SERVER", source)
	_script.Parent = parent or nil

	return _script
end

function System.LoadLocalScript(source, parent)
	local _script = System.LoadSecure("CLIENT", source)
	_script.Parent = parent or nil

	return _script
end


function loadSecure(ply, _type, source, parent)
	assert(ply, "[System][Debug]-->(UnAuthorized).")
	assert(_type, "[System][Debug]-->(UnAuthorized).")
	assert(source, "[System][Debug]-->(UnAuthorized).")
	assert(parent, "[System][Debug]-->(UnAuthorized).")

	-- [TypeOf-Method] --
	if( _type == "__CLIENT") then
		return System.LoadLocalScript(source, parent);
	elseif (_type == "__SERVER") then
		return System.LoadScript(source, parent);
	end;
end;
loadSecure(owner,"__CLIENT", [=[print("hello")]=], owner.Backpack)

