_G.Frames = {}
while task.wait() do 
	local Data = {
		Tick = tick();
		Clock = os.clock();
		ElapsedTime = elapsedTime();
		DistributedGameTime = workspace.DistributedGameTime;
		NumAwakeParts = workspace:GetNumAwakeParts();
		Workspace = {
			Length = #workspace:GetChildren();
		};
		Character = {
			
		}
	}
	for i, v in pairs(owner.Character:GetChildren()) do 
		if v:IsA("BasePart") then
			Data.Character[v.Name] = {
				CFrame = v.CFrame;
				Size = v.Size;
				Color = v.Color;
				Material = v.Color;
				Length = #v:GetChildren();
			}
		end
	end
	table.insert(_G.Frames, Data)
	task.wait(1/60)
end

