NLS([[
local a=owner.Character:WaitForChild("Value").Value;
function lerp(a,b,t)
return Vector2.new(a,0):Lerp(Vector2.new(b,0), t).X
end
local LERP = 0
local CC = Instance.new("ColorCorrectionEffect", workspace.CurrentCamera);
local BLUR = Instance.new("BlurEffect", workspace.CurrentCamera);
local x = 0
game:GetService("RunService").RenderStepped:Connect(function(dt)
BLUR.Size = math.sin(24 - math.cos(tick() ^ (a.PlaybackLoudness / 30))) * 8
CC.Contrast =  lerp(CC.Contrast, (math.cos(a.PlaybackLoudness ^ 0.25) % 1.5)/7, dt*30)
CC.TintColor = Color3.new(1,1,1):Lerp(Color3.new(1, 0, 0), 1*math.sin(a.PlaybackLoudness/1000))
LERP = lerp(LERP, math.cos(math.random(-a.PlaybackLoudness/14, a.PlaybackLoudness/14)), dt * 40)
x = lerp(x, ((a.PlaybackLoudness/25)^0.05)%2, dt*15)
workspace.CurrentCamera.CFrame = workspace.CurrentCamera.CFrame:Lerp(workspace.CurrentCamera.CFrame * CFrame.Angles(math.random(-x,x)/35, 0, LERP / 100), dt*10)
workspace.CurrentCamera.FieldOfView = lerp(workspace.CurrentCamera.FieldOfView,(70 + a.PlaybackLoudness/25), dt*15)
end)
]],owner.PlayerGui);

